mapper.select:
select m.title, m.id, teacher.name, m.send_time, m.has_read
from (select message.title, message.id, message.send_time, message.has_read, message.sender_id from message where message.status = 1 and message.receiver_id = #{reviverId}) as m
left join teacher on m.sender_id = teacher.id where teacher.status = 1 limit #{start}, #{pageSize}
